<?php

/**
 * PlugintdArticle
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 * 
 * @package    tdBlogPlugin
 * @subpackage model
 * @author     Tomasz Ducin <tomasz.ducin@gmail.com>
 * @version    SVN: $Id: Builder.php 6820 2009-11-30 17:27:49Z jwage $
 */
abstract class PlugintdArticle extends BasetdArticle
{
  /**
   * Returns short text of the article.
   *
   * @return String - short text.
   */
  public function getTextShort()
  {
    return tdTools::getMbShortenedString($this->getText(), sfConfig::get('td_blog_short_text_sign_count'));
  }

  /**
   * Activates the article.
   *
   * @return True
   */
  public function activate()
  {
    $this->setActive(true);
    $this->save();
    return true;
  }

  /**
   * Deactivates the article.
   *
   * @return True
   */
  public function deactivate()
  {
    $this->setActive(false);
    $this->save();
    return true;
  }
}